import React from "react";
import axios from "axios";

export default class Signup extends React.Component {
  state = {
    firstname: "",
    lastname:"",
    email: "",
    password:"",
    password2:"",
    city:"",
    name:""
  };

  handleChange = (event) => {
    this.setState({ fristname: event.target.value}); 
  };

  handleCh = (event) => {
    this.setState({ lastname: event.target.value }); 
  };

  handleC = (event) => {
    this.setState({ email: event.target.value }); 
  };

  handlepass = (event) => {
    this.setState({ password: event.target.value }); 
  };

  handlepass2 = (event) => {
    this.setState({ password2: event.target.value }); 
  };

  handlecity = (event) => {
    this.setState({ city: event.target.value }); 
  };

  handlename = (event) => {
    this.setState({ name: event.target.value }); 
  };


  handleSubmit = (event) => {
    event.preventDefault();

    const user = {
      firstname: this.state.fristname,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password,
      password2: this.state.password2,
      city: this.state.city,
      name: this.state.name
    };

    axios
      .post(`https://jsonplaceholder.typicode.com/users`, { user }) //this is where the address to the backend will go
      .then((res) => {
        console.log(res);
        console.log(res.data);
      });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
          <div>
        <label>
          First Name:
          <input 
          id="firstname"
          type="text" 
          name="firstname" 
          class="form-input"
          placeholder="Enter your first name"
          onChange={this.handleChange} />
          </label>
          </div>

          <div>
              <label>
                  Last Name
              </label>
           <input 
          id="lastname"
          type="text" 
          name="name" 
          class="form-input"
          placeholder="Enter your last name"
          onChange={this.handleCh} />

        </div>

        <div>
              <label>
                  Email
              </label>
           <input 
          id="email"
          type="email" 
          name="email" 
          class="form-input"
          placeholder="Enter your email"
          onChange={this.handleC} />


        </div>

        <div>
              <label>
                  Password
              </label>
           <input 
          id="password"
          type="password" 
          name="password" 
          class="form-input"
          placeholder="Enter your password"
          onChange={this.handlepass} />

        </div>

        <div>
              <label>
                  Confirm Password 
              </label>
           <input 
          id="password2"
          type="password" 
          name="password2" 
          class="form-input"
          placeholder="Confirm password"
          onChange={this.handlepass2} />

        </div>

        <div>
              <label>
                 City 
              </label>
           <input 
          id="name"
          type="name" 
          name="name" 
          class="form-input"
          placeholder="Enter your city"
          onChange={this.handlecity} />

        </div>

        <div>
              <label>
                  TagLine 
              </label>
           <input 
          id="name3"
          type="name" 
          name="name" 
          class="form-input"
          placeholder="Enter tagline"
          onChange={this.handlename} />

          <button type="submit">New Profile</button>
        </div>
      </form>
    );
  }
}
