            //    <i class="fas fa-sign-out-alt text-white bg-dark"></i>
            // </button> */}
            <Link
                    to="/login"
                    className="nav-links"
                    onClick={logout}
                    >
                    <i className="fas fa-sign-out-alt"></i>
                  </Link>

        // </form>
    )

};

export default Logout;
