import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './Navbar.css';
// import { Button } from "./Button";


function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

const showButton = () => {
    if (window.innerWidth <= 960) {
        setButton(false);
    } else {
        setButton(true);
    }
};

useEffect(() => {
    showButton()
}, []);

window.addEventListener('resize', showButton);

  return (
    <>
      <nav class="navbar">
        <div class="navbar-container">
          <Link to="/" class="navbar-logo" onClick=
          {closeMobileMenu}>
            TLF <i class="fas fa-utensils" />
          </Link>
          <div class="menu-icon" onClick={handleClick}>
            <i class={click ? "fas fa-times" : "fas fa-bars"} />
          </div>
          <ul class={click ? "nav-menu active" : "nav-menu"}>
            <li class="nav-item">
              <Link to="/" class="nav-links" onClick={closeMobileMenu}>
                <i class="fas fa-home"></i>
              </Link>
            </li>
            <li class="nav-item">
              <Link
                to="/services"
                class="nav-links"
                onClick={closeMobileMenu}
              >
                <i class="far fa-heart"></i>
              </Link>
            </li>
            <li class="nav-item">
              <Link
                to="/products"
                class="nav-links"
                onClick={closeMobileMenu}
              >
                <i class="fas fa-map-marker-alt"></i>
              </Link>
            </li>
            <li class="nav-item">
              <Link
                to="/sign-up"
                class="nav-links-mobile"
                onClick={closeMobileMenu}
              >
                <i class="far fa-user"></i>
              </Link>
            </li>
          </ul>
          {/* {button && <Button buttonStyle='btn--outline'><i class="far fa-user"></i></Button>}  */}
        </div>
      </nav>
    </>
  );
}

export default Navbar;

