import React from "react";
import axios from "axios";
import "../SignUp.css";

export default class Login extends React.Component {
  constructor(props) {
    super(props);

    this.onChangeFirstName = this.onChangeFirstName.bind(this);
    this.onChangeLastName = this.onChangeLastName.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);

    this.onChangeUserName = this.onChangeUserName.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);

    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      firstname: "",
      lastname: "",
      email: "",
      username: "",
      password: "",
    };
  }

  onChangeFirstName(e) {
    this.setState({ firstname: e.target.value });
  }

  onChangeLastName(e) {
    this.setState({ lastname: e.target.value });
  }

  onChangeEmail(e) {
    this.setState({ email: e.target.value });
  }

  onChangeUserName(e) {
    this.setState({ username: e.target.value });
  }

  onChangePassword(e) {
    this.setState({ password: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();

    const userObject = {
      firstname: this.state.firstname,
      lastname: this.state.lastname,
      email: this.state.email,

      username: this.state.username,
      password: this.state.password,
    };

    axios
      .post("http://localhost:5000/users/signup", userObject)
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });

    this.setState({
      firstname: "",
      lastname: "",
      email: "",

      username: "",
      password: "",
    });
  }

  render() {
    return (
      <div className="wrapper">
        <form className="form" onSubmit={this.onSubmit}>
          <h2 className="join">Join the Foodies</h2>
          <div className="form-group">
            <label> First Name</label>
            <input
              type="text"
              value={this.state.firstname}
              onChange={this.onChangeFirstName}
              className="form-control2"
              placeholder="First Name"
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              value={this.state.lastname}
              onChange={this.onChangeLastName}
              className="form-control2"
              placeholder="Last Name"
            />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={this.state.email}
              onChange={this.onChangeEmail}
              className="form-control2"
              placeholder="Email"
            />
          </div>
          <div className="form-group">
            <label>UserName</label>
            <input
              type="text"
              value={this.state.username}
              onChange={this.onChangeUserName}
              className="form-control2"
              placeholder="Username"
            />
          </div>{" "}
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={this.state.password}
              onChange={this.onChangePassword}
              className="form-control2"
              placeholder="Password"
            />
          </div>
          <div className="form-group">
            <button type="submit" value="Login" className="form-input-btn">
              Sign Up
            </button>
            <input type="submit" value="Login" className="btn btn-success btn-block" />
          </div>
        </form>
      </div>
    );
  }
}
