import axios from "axios";
import React, { useState, useEffect } from "react";

const Res = "http://localhost:5000/restaurant/";

export default function Restaurants() {
  const [post, setPost] = useState(null);
//   console.log(post);
  // const [name, setName] = useState('');
  //   console.log(post);   THIS IS THE WORKING ONE

  useEffect(() => {
    axios.get(Res).then((response) => {
      setPost(response.data.restaurants);
      //   setName(response)
        console.log(response.data.restaurants)
    });
  }, []);

  if (!post) return null;

  const currentRests = post.map((rest, reviews) => (
    <li key={reviews}>
    <h1>{rest.name}</h1>
      <h1>{rest.reviews}</h1>
    </li>
  ));

  return (


    <ul>
        <h1>This is where Restaurant reviews can be displayed..</h1>
      {currentRests}
      {/* <li>{name}</li> */}
      {/* <li>{post.location}</li>
      <li>{post.hours}</li>
      <li>{post.rating}</li> */}
    </ul>
  );
}
