import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";
import { Button } from "./Button";
import { Button2 } from "./Button2";
import SearchBar from "./searchbar";

function Navbar({handleSubmit, query, findRestaurant}) {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener("resize", showButton);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
          <img className="navbar-logo" src="../TLFLogo.png" alt="Logo" style="12px"/>
          </Link>
          <div className="menu-icon" onClick={handleClick}>
            <i className={click ? "fas fa-times" : "fas fa-bars"} />
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/" className="nav-links" onClick={closeMobileMenu}>
                <i className="fas fa-home"></i>
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/favorites"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                <i className="far fa-heart"></i>
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/map"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                <i className="fas fa-map-marker-alt"></i>
              </Link>
            </li>
            <li className="">
              <Link
                to="/signup"
                className="nav-links-mobile"
                onClick={closeMobileMenu}
              >
                <i className="far fa-user"></i>
              </Link>
            </li>
            <li className="">
              <Link
                to="/"
                className="nav-links-mobile"
                onClick={closeMobileMenu}
              >
                <i className="fas fa-sign-out-alt"></i>
              </Link>
            </li>
          </ul>
          {button && <Button buttonStyle="btn--outline"></Button>}
          {button && <Button2 buttonStyle="btn--outline"></Button2>}

          <SearchBar handleSubmit={handleSubmit} query={query}  findRestaurant={findRestaurant}/>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
