import React from "react";
import axios from "axios";

export default class Signup extends React.Component {
  state = {
    name: "",
    email: "",
  };

  handleChange = (event) => {
    this.setState({ name: event.target.value, email: event.target.value }); //add email, password, etc..
  };

  handleSubmit = (event) => {
    event.preventDefault();

    const user = {
      name: this.state.name,
      email: this.state.email,
    };

    axios
      .post(`https://jsonplaceholder.typicode.com/users`, { user }) //this is where the address to the backend will go
      .then((res) => {
        console.log(res);
        console.log(res.data);
      });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Person Name:
          <input 
          id="username"
          type="text" 
          name="name" 
          class="form-input"
          placeholder="Enter your irst name"
          onChange={this.handleChange} />
