import axios from "axios";
import React, { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button';
import CardGroup from 'react-bootstrap/CardGroup'

const Res = "http://localhost:5000/restaurant/";

export default function Home() {
  const [post, setPost] = useState(null);
  console.log(post);
  // const [name, setName] = useState('');
  //   console.log(post);   THIS IS THE WORKING ONE

  useEffect(() => {
    axios.get(Res).then((response) => {
      setPost(response.data.restaurants);
      //   setName(response)
        console.log(response.data.restaurants);
    });
  }, []);

  if (!post) return null;


  const currentReviews = post[0].reviews.map((rest, id ) => (
    <li key={id}>

    <p>{rest.username}</p>
      <p>{rest.review}</p>

    </li>


  ));

  const currentRests = post.map((rest, id) => (
    <ul key={id}> 
<CardGroup>
  <Card>
    <Card.Img variant="top" src="holder.js/100px160" />
    <Card.Body>
      <Card.Title>{rest.name}</Card.Title>
      <Card.Text>
        This is a wider card with supporting text below as a natural lead-in to
        additional content. This content is a little bit longer.
      </Card.Text>
    </Card.Body>
    <Card.Footer>
      <small className="text-muted">Last updated 3 mins ago</small>
    </Card.Footer>
  </Card>
  <Card>
    <Card.Img variant="top" src="holder.js/100px160" />
    <Card.Body>
      <Card.Title>Card title</Card.Title>
      <Card.Text>
        This card has supporting text below as a natural lead-in to additional
        content.{' '}
      </Card.Text>
    </Card.Body>
  </Card>
</CardGroup>

      {/* <Card style={{ width: '18rem' }}>
  <Card.Img variant="top" src="holder.js/100px180" />
  <Card.Body>
    <Card.Title>{rest.name}</Card.Title>
    <Card.Text>
    {currentReviews}
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
</Card> */}
</ul>
  ));




  return (
      <ul>
    {currentRests}

    </ul>




  );
}
