import React from "react";
import Navbar from "./Components/Navbar";
import { BrowserRouter as Router, Switch, Route} from "react-router-dom";
import "./App.css";


import Signup from "./Components/pages/SignUp";
import Get from "./Components/pages/Get";
import Delete from "./Components/pages/Delete";
import Put from "./Components/pages/PersonPut";
import Login from "./Components/pages/Login";
import Home from "./Components/pages/Home";

class App extends Component {
  async requestToken(data) {
      await fetch(`https://neighborapp-backend.herokuapp.com/sessions/${data}`, {
          method: 'GET',
          headers: {
              'Content-Type': 'application/json'
          }
      })
          .then(response => response.json())
          .then(data => {
              if (!data.status === 'SUCCESS') {
                  localStorage.removeItem('token')
              }
          })
  }

  componentDidMount = () => {
      this.requestToken(localStorage.getItem('token'))
  }

  render() {
      if (localStorage.getItem('token') != null) {
          return (
              <div className="App">
                 <Route path="/Login" exact component={Login} />
          <Route path="/Get" exact component={Get} />
          <Route path="/signup" exact component={Signup} />
          <Route path="/Put" exact component={Put} />
          <Route path="/Delete" exact component={Delete} />
          <Route path="/" exact component={Home} />
                  <Navbar />
              </div>
          )
      } else {
          return <Login />
      }
  }
}

export default App

