import axios from "axios";

const API_URL = "http://localhost:5000/api/";
const token =
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VybmFtZSI6IlBIZXJuIiwiX2lkIjoiNjE2ZDUwNDI1Y2FiMGJkYmViZWU3NmI1IiwiaWF0IjoxNjM0OTMxNDQ2LCJleHAiOjE2MzUwMDM0NDZ9.yca_2yFx6b1dljKDG0GrxD5L0SdMbTnyVH2mgJBBYPg";

class UserService {
  async getProfile() {
    return await axios.get(API_URL + "user/profile", {
      headers: { Authorization: `Bearer ${token}` },
    });
  }
}

export default new UserService();
