const Res = "http://localhost:5000/restaurant/";

export default function Restaurants() {
  const [post, setPost] = useState(null);
  console.log(post);
  // const [name, setName] = useState('');
  //   console.log(post);   THIS IS THE WORKING ONE

  useEffect(() => {
    axios.get(Res).then((response) => {
      setPost(response.data.restaurants);
      //   setName(response)
        // console.log(response.data.restaurants)
    });
  }, []);

  if (!post) return null;

  const currentRests = post.map((rest, id) => (
    <li key={id}>

      <h1>{}</h1>
    </li>
  ));

  const currentReviews = post[0,2].reviews.map((rest, id ) => (
    <li key={id}>
          <h1>{rest.name}</h1>
    <h1>{rest.username}</h1>
      <h1>{rest.review}</h1>

    </li>


  ));
//   const currentRe = post[2].reviews.map((rest, id ) => (
//     <li key={id}>
//           <h3>{rest.name}</h3>
//     <h3>{rest.username}</h3>
//       <h3>{rest.review}</h3>

//     </li>


//   ));


  return (

    <div>
    <ul>
        <h1>This is where Restaurant reviews can be displayed..</h1>
      {currentRests}
      {/* <li>{name}</li> */}
      {/* <li>{post.location}</li>
      <li>{post.hours}</li>
      <li>{post.rating}</li> */}
    </ul>
    <div>
        {currentReviews}
        {/* {currentRe} */}
    </div>
    </div>
  );
}
