import React from "react";
import axios from "axios";

export default class Newres extends React.Component {
  constructor(props) {
    super(props);

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeReview = this.onChangeReview.bind(this);
    this.onChangeRating = this.onChangeRating.bind(this);

    this.state = {
      title: "",
      review: "",
      rating: ""
    };
  }

  onChangeTitle(e) {
    this.setState({ title: e.target.value });
  }

  onChangeReview(e) {
    this.setState({ review: e.target.value });
  }

  onChangeRating(e) {
    this.setState({ rating: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();

    const resObject = {
      title: this.state.title,
      review: this.state.review,
      rating: this.state.rating,
    }; 

    axios
      .post("http://localhost:5000/review/addNewReview/", resObject)
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });

    this.setState({ title: "", review: "", rating:"" });
  }

  render() {
    return (

      <div className="wrapper" style={{margin:"20px"}}>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label><h2>Title</h2></label>
            <input
              type="text"
              value={this.state.title}
              onChange={this.onChangeTitle}
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label><h2>Review</h2></label>
            <textarea   type="text"
              value={this.state.review}
              onChange={this.onChangeReview}
              className="form-control">

             </textarea>
          </div>
          <div className="form-group">
            <label><h2>Rating</h2></label>
            <input
              type="number"
              value={this.state.rating}
              onChange={this.onChangeRating}
              className="form-control"
            />
          </div>
          <div className="form-group">

            <input
              type="submit"
              value="Submit"
              className="btn btn-success btn-block"
            />
          </div>
        </form>
      </div>
    );
  }
}
